mapper.update:
UPDATE DEP
   SET MDFY_DT = SYSDATE
     , MDFYR = #{mdfyr}
     , DEP_NM = #{depNm}
     , DEP_HD_ID = #{depHdId}
     , DEP_CRT_DT = TO_DATE(#{depCrtDt}, 'YYYY-MM-DD')
    
     , USE_YN = NVL(#{useYn}, 'N')	
    
 WHERE DEP_ID = #{depId}

 	UPDATE DEP
	   SET DEL_YN = 'Y'
	 WHERE DEP_ID = #{_parameter}
