mapper.select:
SELECT E.EMP_ID, E.F_NM, E.L_NM, E.EML, E.PHN, E.PRFL_PHT, E.EMPLMNT_STTS, E.DEP_ID, J.JOB_NM, D.DEP_NM, P.PSTN_NM FROM (SELECT * FROM EMP WHERE DEL_YN = 'N'
AND DEP_ID = #{depId}
AND F_NM LIKE '%' || #{fNm} || '%' ) E JOIN EMP CE ON CE.EMP_ID = E.CRTR JOIN EMP ME ON ME.EMP_ID = E.MDFYR JOIN DEP D ON D.DEP_ID = E.DEP_ID JOIN JOB J ON J.JOB_ID = E.JOB_ID JOIN PSTN P ON P.PSTN_ID = E.PSTN_ID

mapper.update:
UPDATE EMP
   SET MDFY_DT = SYSDATE,
	   MDFYR = #{mdfyr},


	   PWD = #{pwd},
	   PWD_SALT = #{pwdSalt}


	   PSTN_ID = #{pstnId}


	   JOB_ID = #{jobId}


	   DEP_ID = #{depId}


	   F_NM = #{fNm},
	   BRTHDY = #{brthdy},
	   EML = #{eml},
	   PHN = #{phn},
	   ADDRSS = #{addrss},
	   DTL_ADDRSS =#{dtlAddrss},
	   PST_CD = #{pstCd},
	   L_NM = #{lNm},

	   PRFL_PHT = #{prflPht}



 WHERE EMP_ID = #{empId}
   AND DEL_YN = 'N'

UPDATE EMP
   SET DEL_YN = 'Y'
 WHERE EMP_ID = #{empId}

UPDATE EMP
   SET LTST_LGN_IP = #{ltstLgnIp}
   	 , LTST_LGN_DT = SYSDATE
   	 , LGN_CNT = 0
   	 , FAIL_DT = ''
 WHERE EMP_ID = #{empId}

UPDATE EMP
   SET PSTN_ID = #{pstnId}
   	 , MDFYR = #{mdfyr}
 WHERE EMP_ID IN

  #{empId}

UPDATE EMP
   SET JOB_ID = #{jobId}
   	 , MDFYR = #{mdfyr}
 WHERE EMP_ID IN

  #{empId}

UPDATE EMP
   SET DEP_ID = #{depId}
   	 , MDFYR = #{mdfyr}
 WHERE EMP_ID IN

  #{empId}

UPDATE EMP
   SET EMPLMNT_STTS = #{emplmntStts}
   	 , MDFYR = #{mdfyr}
 WHERE EMP_ID IN

  #{empId}
