mapper.select:
SELECT EQP_ID , CRT_DT , CRTR , MDFY_DT , MDFYR , USE_YN , DEL_YN , EQP_NM , EQP_TP , APPL_STTS , APPL_ID , EQP_PRC , TO_CHAR(PRCHS_DT, 'YYYY-MM-DD') PRCHS_DT , LOSS_STTS , TO_CHAR(LOSS_RPRT_DT, 'YYYY-MM-DD') LOSS_RPRT_DT , TO_CHAR(APPL_DT, 'YYYY-MM-DD') APPL_DT FROM EQP WHERE DEL_YN = 'N' AND USE_YN = 'Y' AND EQP_ID IN
#{eqpId}
ORDER BY EQP_ID ASC

mapper.update:
UPDATE EQP
   SET APPL_STTS= #{applStts}
     , APPL_ID = #{applId}
     , APPL_DT= SYSDATE
     , LOSS_STTS = NVL(#{lossStts}, 'N')
 WHERE EQP_ID= #{eqpId}

UPDATE EQP
   SET MDFY_DT= SYSDATE
     , MDFYR= #{mdfyr}
     , USE_YN= NVL(#{useYn}, 'N')
     , EQP_NM= #{eqpNm}
     , EQP_TP= #{eqpTp}
     , APPL_STTS= #{applStts}
     , EQP_PRC= #{eqpPrc}
     , PRCHS_DT= #{prchsDt}
     , LOSS_STTS= NVL(#{lossStts}, 'N')
     , LOSS_RPRT_DT= #{lossRprtDt}
     , APPL_DT= #{applDt}
 WHERE EQP_ID= #{eqpId}

   AND PRCHS_DT <= #{applDt}


   AND PRCHS_DT <= #{lossRprtDt}

UPDATE EQP
   SET DEL_YN= 'Y'
 WHERE EQP_ID= #{eqpId}
