mapper.update:
UPDATE EQP
   SET MDFY_DT= SYSDATE
     , MDFYR= #{mdfyr}
     , USE_YN= NVL(#{useYn}, 'N')
     , EQP_NM= #{eqpNm}
     , EQP_TP= #{eqpTp}
     , APPL_STTS= #{applStts}
     , EQP_PRC= #{eqpPrc}
     , PRCHS_DT= #{prchsDt}
     , LOSS_STTS= NVL(#{lossStts}, 'N')
     , LOSS_RPRT_DT= #{lossRprtDt}
     , APPL_DT= #{applDt}
 WHERE EQP_ID= #{eqpId}

   AND PRCHS_DT <= #{applDt}


   AND PRCHS_DT <= #{lossRprtDt}

UPDATE EQP
   SET DEL_YN= 'Y'
 WHERE EQP_ID= #{eqpId}
