mapper.select:
SELECT I.ISSU_ID PK_COL, DENSE_RANK() OVER(ORDER BY I.CRT_DT || I.ISSU_ID DESC) RNUM, I.ISSU_ID, I.CRT_DT, I.CRTR, I.MDFY_DT, I.MDFYR, I.USE_YN, I.DEL_YN, I.REQ_ID, I.ISSU_TTL, I.ISSU_CNTNT, I.VW_CNT, I.DFFCLTY, I.DTL_CNTNT, I.MN_TM_MBR_ID, I.STTS, R.REQ_TTL, CE.F_NM CE_F_NM, CE.L_NM CE_L_NM, ME.F_NM ME_F_NM, ME.L_NM ME_L_NM FROM (SELECT * FROM ISSU WHERE DEL_YN = 'N'
AND REQ_ID = #{reqId}
/*조건 추가*/) I JOIN REQ R ON R.REQ_ID = I.REQ_ID JOIN EMP CE ON CE.EMP_ID = I.CRTR JOIN EMP ME ON ME.EMP_ID = I.MDFYR

mapper.update:
UPDATE ISSU
   SET MDFY_DT = SYSDATE,
	   MDFYR = #{mdfyr},
	   ISSU_TTL = #{issuTtl},
	   ISSU_CNTNT = #{issuCntnt},
	   DFFCLTY = #{dffclty},
	   DTL_CNTNT = #{dtlCntnt}

	 , MN_TM_MBR_ID = #{mnTmMbrId}


	 , STTS = #{stts}

 WHERE ISSU_ID = #{issuId}

UPDATE ISSU
   SET VW_CNT = VW_CNT+1
 WHERE ISSU_ID = #{_parameter}

UPDATE ISSU
   SET DEL_YN = 'Y'
 WHERE ISSU_ID = #{_parameter}
