mapper.update:
UPDATE PRJ
   SET MDFY_DT = SYSDATE
   	 , MDFYR = #{mdfyr}

   	 , USE_YN = NVL(#{useYn}, 'N')


   	 , PRJ_NM = #{prjNm}


   	 , CSTMR = #{cstmr}


   	 , STRT_DT = #{strtDt}


   	 , END_DT = #{endDt}


   	 , PRJ_STTS =#{prjStts}

 WHERE PRJ_ID = #{prjId}

UPDATE PRJ
   SET DEL_YN = 'Y' 
 WHERE PRJ_ID = #{_parameter}
