mapper.select:
SELECT TO_CHAR(PL.CRT_DT, 'YYYYMMDDHH24MISS') || PL.EMP_ID PK_COL , DENSE_RANK() OVER(ORDER BY PL.CRT_DT || PL.EMP_ID DESC) RNUM , PL.EMP_ID , TO_CHAR(PL.CRT_DT, 'YYYY-MM-DD HH24:MI:SS') CRT_DT , PL.CRTR , TO_CHAR(PL.CHNG_DT, 'YYYY-MM-DD HH24:MI:SS') CHNG_DT , PL.PRVS_PSTN_ID , P.PSTN_NM PRVS_PSTN_NM , PL.CHNG_PSTN_ID , PT.PSTN_NM CHNG_PSTN_NM , PL.CHNG_RSN , E.F_NM , E.L_NM FROM PSTN_LOG PL INNER JOIN PSTN P ON P.PSTN_ID = PL.PRVS_PSTN_ID INNER JOIN PSTN PT ON PT.PSTN_ID = PL.CHNG_PSTN_ID INNER JOIN EMP E ON E.EMP_ID = PL.EMP_ID
WHERE PL.EMP_ID = #{empId}
AND PL.CHNG_DT >= TO_DATE(#{startDt} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND PL.CHNG_DT <= TO_DATE(#{endDt} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
