mapper.update:
UPDATE REQ
   SET MDFY_DT=SYSDATE
     , MDFYR=#{mdfyr}
     , USE_YN=NVL(#{useYn}, 'N')
     , DTL_REQ=#{dtlReq}
     , STRT_DT=#{strtDt}
     , EXPCT_END_DT=#{expctEndDt}
     , ATTCH=#{attch}
     , PRJ_ID=#{prjId}
     , MN_DVLPR=#{mnDvlpr}
     , TST_RSLT=#{tstRslt}
     , TSK_STTS=#{tskStts}
     , PRCS_STTS=#{prcsStts}
     , PRRTY=#{prrty}
     , REQ_TTL=#{reqTtl}
     , REQ_CNFR_NM=#{reqCnfrNm}
 WHERE REQ_ID=#{reqId}

	UPDATE REQ
   SET DEL_YN='Y'
 WHERE REQ_ID=#{_parameter}

UPDATE REQ
   SET DEL_YN='Y'
 WHERE prjId = #{_parameter}
