mapper.update:
UPDATE TM
   SET MDFY_DT = SYSDATE
     , MDFYR = #{mdfyr}
     , TM_NM = #{tmNm}
     , TM_CRT_DT = TO_DATE(#{tmCrtDt}, 'YYYY-MM-DD')
     , DEP_ID = #{depId}
     , TM_HD_ID = #{tmHdId}

     , USE_YN = NVL(#{useYn}, 'N')	

 WHERE TM_ID = #{tmId}

UPDATE TM
   SET DEL_YN = 'Y'
 WHERE TM_ID = #{_parameter}
